import pytest

from crysp.keccak import *

# testing standard Keccak Sponge:
# -------------------------------

# test case obtained by running KeccakReferenceandOptimized
# see output 'KeccakSpongeIntermediateValues_r1024c576.txt'
# input message (bitlen 29)

def test_keccak_001():
    h = Keccak(r=1024,c=576)
    m1 = "\x53\x58\x7b\xc8"
    # test message-to-blocks iterator
    blks = []
    for b in h.iterblocks(m1,bitlen=29):
        assert len(b)==1024
        blks.append(b)
    assert len(blks)==1
    # check 10*1 padding (when bitlen not a multiple of 8)
    assert blks[0][:32] == 0x397b5853
    assert blks[0][-1] == 1
    assert blks[0][33:-1] == 0

# test some big digest values:
def test_keccak_002():
    h = Keccak(r=1024,c=576)
    h.outlen = 4096
    assert h('\xc0',bitlen=2) == "".decode('hex')

def test_keccak_003():
    h = Keccak(r=1024,c=576,len=4096)
    m1 = "\x53\x58\x7b\xc8"
    assert h(m1,bitlen=29) == "".decode('hex')

def test_keccak_004():
# test sponge with longer input message:
    h = Keccak(r=1024,c=576,len=4096)
    m2 = "".decode('hex')
    assert h(m2,bitlen=2008) == "".decode('hex')

# test sponge with another capacity value:
def test_keccak_005():
    h2 = Keccak(r=1344,c=256,len=4096)
    assert h2('',bitlen=0) == "".decode('hex')

# Compact Keccak Sponge tests:
# ----------------------------
def test_keccak_006():
    h3 = Keccak(r=512,c=288,len=512)
    assert h3('\x48',bitlen=5) == "BF4D7E53D63D9FEB016FCD2FE2F38DEB3A1435FE40C226C495C28820F82BE568B7ABC7FF750571B23E714B66BD1DFA4B0D0E23856D40875C6E5BE50831F6BB35".decode('hex')

def test_keccak_007():
    h4 = Keccak(b=200,r=40,len=160)
    #Len = 43
    #Msg = F219BD629820
    #Squeezed = C8F9476DBF0B0FE01F80629FD5689097AAAC6732
    assert h4.c == 160
    assert h4("F219BD629820".decode('hex'),bitlen=43) == "C8F9476DBF0B0FE01F80629FD5689097AAAC6732".decode('hex')

# Duplex construction tests:
# --------------------------
def test_keccak_008():
    h5 = Keccak(b=1600,r=1027,outlen=1027)
    # test padding (WARNING: Duplex construction does not follow NIST bitorder convention !? WTF...)
    m123 = (''.join("89 31 A3 8D FE DE FE DC 2F 3E 0A 7C 03 90 E6 04".split())).decode('hex')
    blks = []
    for b in h5.iterblocks(m123,bitlen=123):
        assert len(b)==1027
        blks.append(b)
    assert len(blks)==1
    assert blks[0][123] == 1
    assert blks[0][-1] == 1
    assert h5.duplex('',bitlen=0) == "E6F80B3637E0F7D50F4CD36C3A293AD320205E2187AC60CA9A24E26B5104931C3FB2897BA0FEDD529099CBA8625EFC73879C28DA8C1D09CD9282662C3844825F93C05E5A7C00313DC95C6613C42484A5CC298CE493607DBE80328FE176CC86376678572888E411196C82118183E59434B7D3AD5A92168BFCC3602DAD16735B5C06".decode('hex')
    assert h5.duplex('\x00',bitlen=1) == "A4AC5C6E75D41EAFAA6B9E261FFBC14DABD73C31044C7822CE5DBF37DE03995CBA565107F2C86E35260BEED3F83DC577CE499C43F4F916D4605673ED827F52C95E63EC69E2D3C6428327B9DDA1DB4F5EA5579EB96F73C83BB167345561BE2E7AF35C8050639866373372F7DE4B9AA8BC0131B3A8BCF53F8299B26BF44C26C18E03".decode('hex')
    assert h5.duplex('\x03',bitlen=2) == "F8B98D98CE69529838CDCDC76BEC0A88F61FD229828CC227B8EAD5A000467FCC3708510A50A65F2060BCBDF32672A876B7C1BBD6519208D5852A2972CEC57A52321C469419BBC6FAD0DBCC9FC56D1C6BACA421F67FEFD4AA26410A048417AE7CD8DD19C0679E19F160AA8DA51AE29C42C59E2BAFC5A699095050EAB008C3959A01".decode('hex')

# SHA-3 tests:
# ------------
#Len = 2111
#Msg = 
#MD = 4A1B83F269251D71E1B4D65533795992CBE4F2501AE84901F41E9325492F962FA95D49D8676B017FC7C3711775ECDEEA8B22E0D7DDA67BC926B83D9DC425CE30
